var Code=require("code"),Hawk=require("../lib"),Lab=require("lab"),internals={},lab=exports.lab=Lab.script(),describe=lab.experiment,it=lab.test,expect=Code.expect;describe("Crypto",function(){describe("generateNormalizedString()",function(){it("should return a valid normalized string",function(e){expect(Hawk.crypto.generateNormalizedString("header",{ts:1357747017,nonce:"k3k4j5",method:"GET",resource:"/resource/something",host:"example.com",port:8080})).to.equal("hawk.1.header\n1357747017\nk3k4j5\nGET\n/resource/something\nexample.com\n8080\n\n\n"),e()}),it("should return a valid normalized string (ext)",function(e){expect(Hawk.crypto.generateNormalizedString("header",{ts:1357747017,nonce:"k3k4j5",method:"GET",resource:"/resource/something",host:"example.com",port:8080,ext:"this is some app data"})).to.equal("hawk.1.header\n1357747017\nk3k4j5\nGET\n/resource/something\nexample.com\n8080\n\nthis is some app data\n"),e()}),it("should return a valid normalized string (payload + ext)",function(e){expect(Hawk.crypto.generateNormalizedString("header",{ts:1357747017,nonce:"k3k4j5",method:"GET",resource:"/resource/something",host:"example.com",port:8080,hash:"U4MKKSmiVxk37JCCrAVIjV/OhB3y+NdwoCr6RShbVkE=",ext:"this is some app data"})).to.equal("hawk.1.header\n1357747017\nk3k4j5\nGET\n/resource/something\nexample.com\n8080\nU4MKKSmiVxk37JCCrAVIjV/OhB3y+NdwoCr6RShbVkE=\nthis is some app data\n"),e()})})});